package gt.seguridad.pojo.chart;

public class Legend {

	private String align;
	private String vertitalAlign;
	
	public Legend() {
		// TODO Auto-generated constructor stub
	}

	public Legend(String align, String vertitalAlign) {
		super();
		this.align = align;
		this.vertitalAlign = vertitalAlign;
	}

	public String getAlign() {
		return align;
	}

	public void setAlign(String align) {
		this.align = align;
	}

	public String getVertitalAlign() {
		return vertitalAlign;
	}

	public void setVertitalAlign(String vertitalAlign) {
		this.vertitalAlign = vertitalAlign;
	}
	
	
}
